import React from 'react'

class controlButton extends React.Component {

  constructor() {
    super()
    this.state = {
      control: null,
    }
    this.handlecontrol = this.handlecontrol.bind(this)
  }

  componentsDidMount() {
    return null
  }

  handlecontrol() {
    this.setState (
      function() {
        if(this.state.control) {
          return {
            control: false
          }
        }
        else {
          return {
            control: true
          }
        }
      }
    )
  }

  render() {
    if(this.state.control) {
      return (
        <div>
          <div className="mainDiv">
            <button className="stopButton" onClick={this.handlecontrol}><h3>Stop</h3></button>
          </div>
          <div className="mainDiv">
            <h3>Status: {this.state.control}</h3>
          </div>
        </div>
    )}
    else {
      return (
        <div>
          <div className="mainDiv">
            <button className="startButton" onClick={this.handlecontrol}><h3>Start</h3></button>
          </div>
          <div className="mainDiv">
            <h3>Status: {this.state.control}</h3>
          </div>
        </div>
    )}
  }

}

export default controlButton
