import React from 'react'

class Login extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      username: '',
      password: '',
      valid: false
    }
    this.changeUsername = this.changeUsername.bind(this)
    this.changePassword = this.changePassword.bind(this)
    this.verifyLogin = this.verifyLogin.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
  }

  changeUsername(event) {
   this.setState({username: event.target.value})
  }

  changePassword(event) {
   this.setState({password: event.target.value})
  }

  verifyLogin(event) {
    event.preventDefault()
    this.setState(
      function() {
        return {
          valid: true
        }
      }
    )
    console.log('verify')
    this.sendLogin()
  }

  sendLogin() {
    // const {getCreds} = this.props
    this.props.getCreds(this.state.valid)
  }

  render() {
    return (
      <div className="mainDiv">
        <form onSubmit={this.verifyLogin} className="Login">
          <label>
            <input type="text" placeholder="Username" value={this.state.username} onChange={this.changeUsername} />
          </label>
          <label>
            <input type="password" placeholder="Password" value={this.state.password} onChange={this.changePassword} />
          </label>
          <input type="submit" value="Login" />
        </form>
      </div>
    )
  }

}

export default Login
