import React from 'react'
import PropTypes from "prop-types"
import { serverInfo } from "../../../actions/serverInfo"
import { connect } from "react-redux"

class Server extends React.Component {

  constructor() {
    super()
    this.state = {
      serverName: null
    }
  }

  componentDidMount() {
    const requestData = {
      name: "Dedicated Server"
    }
    console.log(this.props.serverInfo(requestData))
    this.setState({ serverName: this.props.serverInfo(requestData) })
  }

  render() {
    return (
      <div className="headerCard">
        <h1>{this.state.serverName}</h1>
      </div>
    )
  }

}

Server.propTypes = {
  serverInfo: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired
}
const mapStateToProps = state => ({
  errors: state.errors
})
export default connect(
  mapStateToProps,
  { serverInfo }
)(Server)
