import React from 'react'

class Address extends React.Component {

  constructor() {
    super()
    this.state = {
      url: '',
      name: '',
      address: '',
      port: null,
      players: null,
      version: null
    }
  }

  componentDidMount() {
    this.setState (
      function() {
        // api request, populate data
        return {
          url: this.props.baseURL
        }
      }
    )
  }

  render() {
    return (
      <div>
        <h2>Status</h2>
        <h3>Name: </h3><div className="code"><code>{this.state.name}</code></div>
        <h3>Address: </h3><div className="code"><code>{this.state.address}</code></div>
        <h3>Port: </h3><div className="code"><code>{this.state.port}</code></div>
        <h3>Players: </h3><div className="code"><code>{this.state.players}</code></div>
        <h3>Software: </h3><div className="code"><code>{this.state.software}</code></div>
        <h3>Version: </h3><div className="code"><code>{this.state.version}</code></div>
      </div>
    )
  }

}

export default Address
