import axios from "axios"
import { GET_SERVER_ERRORS } from "./types"

// Get server info
export const serverInfo = serverName => dispatch => {
  axios
    .post("/api/server/info", serverName)
    .then(res => {
      return res.data
    })
    .catch(err =>
      dispatch({
        type: GET_SERVER_ERRORS,
        payload: err.response.data
      })
    )
}
