import React from 'react'
import Header from './components/Header'
import statusButton from './components/statusButton'

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      address: '3.128.29.141',
    }
    this.getAddress = this.getAddress.bind(this)
  }

  getAddress() {
    return null
  }

  render() {

      if(this.state.status) {
        return (
          <div>
            <Header />
            <div className="mainDiv">
              <h3>Status: Started</h3>
              <button className="stopButton" onClick={this.handleStatus}>Stop</button>
            </div>
          </div>
      )}

      else {
        return (
          <div>
            <Header />
            <div className="mainDiv">
              <h3>Status: Stopped</h3>
              <button className="startButton" onClick={this.handleStatus}>Start</button>
            </div>
          </div>
      )}

  }

}

export default App;
