import React from 'react'

class StartStop extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      classname: "startButton",
      text: "Start"
    }
  }

  componentDidMount() {
  }

  handleClick() {
    alert('Click!')
  }

  render() {
    if(this.props.status === "Online"){
      this.setState({
        classname: "stopButton",
        text: "Stop"
      })
    }
    return (
      <div>
        <button className={this.state.classname} onClick={this.handleClick}><h3>{this.state.text}</h3></button>
      </div>
    )
  }

}

export default StartStop
