import React from "react"
import PropTypes from "prop-types"
import { connect } from "react-redux"
import { logoutUser } from "../../actions/authActions"
import axios from "axios"
// import { serverInfo } from "../../actions/serverInfo"
import { GET_SERVER_ERRORS } from "../../actions/types"

import Server from './components/Server'
import Status from './components/Status'
import Connect from './components/Connect'
import Players from './components/Players'
import Software from './components/Software'
import Share from './components/Share'

class Dashboard extends React.Component {
  constructor() {
    super()
    this.state = {
      name: "Unknown",
      status: "Unknown",
      address: "Unknown",
      port: "Unknown",
      onlinePlayers: "Unknown",
      maxPlayers: "Unknown",
      gamemode: "Unknown",
      difficulty: "Unknown",
      seed: "Unknown",
      software: "Unknown",
      version: "Unknown"
    }
  }

  onLogoutClick = e => {
    e.preventDefault()
    this.props.logoutUser()
  }

  onProfileClick = e => {
    e.preventDefault()
    alert('Profile Click')
  }

  componentDidMount() {
    axios
      .get("/api/servers/info", {
        params: {
          name: "Dedicated Server"
        }
      })
      .then(res => {
        console.log(res.data.data.name)
        const name  = res.data.data.name
        console.log(name)
      })
      .catch(err =>
        dispatch({
          type: GET_SERVER_ERRORS,
          payload: err.response.data
        })
      )
  }

  render() {
    const { user } = this.props.auth
    return (
      <div className="appDiv">
        <nav className="navtop">
          <div>
            <h1>Bedrock Server Controls</h1>
            <button onClick={this.onProfileClick}><i className="fas fa-user-circle"></i>{user.username}</button>
            <button onClick={this.onLogoutClick}><i className="fas fa-sign-out-alt"></i>Logout</button>
          </div>
        </nav>
        <div className="headerCards">
          <Server name={this.state.name}/>
        </div>
        <div className="cards">
          <Status status={this.state.status}/>
          <Connect address={this.state.address} port={this.state.port}/>
          <Players onlinePlayers={this.state.onlinePlayers} maxPlayers={this.state.maxPlayers}/>
          <Software software={this.state.software} version={this.state.version}/>
          <Share baseURL="http://3.128.29.141"/>
        </div>
      </div>
    )
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
}
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
})
export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard)
