import React from 'react'
import Header from './components/Header'
import StatusButton from './components/StatusButton'
import Address from './components/Address'
import Port from './components/Port'
import Share from './components/Share'
import Login from './components/Login'
import { VerifyLogin } from './components/VerifyLogin'

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      url: 'http://3.128.29.141',
      login: false,
      address: '',
      port: '',
      username: '',
      password: ''
    }
    this.receiveLogin = this.receiveLogin.bind(this)
  }

  receiveLogin(loginUsername, loginPassword) {
    this.setState(
      function() {
        return {
          username: loginUsername,
          password: loginPassword,
          login: VerifyLogin(this.state.url, this.state.username, this.state.password)
        }
      }
    )
  }

  render() {

    const headerStyle = {
      width: '100%',
      fontSize: 30,
      fontWeight: 'bold',
      marginBottom: 15,
      textAlign: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignCtems: 'center',
      padding: '3%',
    }

    if(this.state.login === true) {
      return (
        <div>
          <Header />
          <StatusButton />
          <Address address={this.state.address} />
          <Port port={this.state.port} />
          <Share baseURL={this.state.url} />
        </div>
    )}
    else if(this.state.login === false){
      return (
        <div className="appDiv">
          <h1 style={headerStyle}>Bedrock Server Controls</h1>
          <Login getCreds={this.receiveLogin}/>
        </div>
    )}
  }

}

export default App
