import React from 'react'
import Header from './components/Header'
import StatusButton from './components/StatusButton'
import Address from './components/Address'
import Port from './components/Port'
import Share from './components/Share'
import Login from './components/Login'
import * as VerifyLogin from './components/VerifyLogin'

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      login: false,
      address: '',
      port: '',
      username: '',
      password: ''
    }
    this.getAddress = this.getAddress.bind(this)
    this.receiveLogin = this.receiveLogin.bind(this)
  }

  receiveLogin(loginUsername, loginPassword) {
    this.setState(
      function() {
        return {
          username: loginUsername,
          password: loginPassword
        }
      }
    )

  }

  render() {
    if(this.state.login === true) {
      return (
        <div>
          <Header />
          <StatusButton />
          <Address address={this.state.address} />
          <Port port={this.state.port} />
          <Share address={this.state.address} />
        </div>
    )}
    else if(this.state.login === false){
      return (
        <div>
          <Header />
          <Login getCreds={this.receiveLogin}/>
        </div>
    )}
  }

}

export default App
