import React from 'react'

class StatusButton extends React.Component {

  constructor() {
    super()
    this.state = {
      status: null,
    }
    this.handleStatus = this.handleStatus.bind(this)
  }

  componentsDidMount() {
    return null
  }

  handleStatus() {
    this.setState (
      function() {
        if(this.state.status === 'Started') {
          return {
            status: 'Stopped'
          }
        }
        else {
          return {
            status: 'Started'
          }
        }
      }
    )
  }

  render() {
    if(this.state.status === 'Started') {
      return (
        <div>
          <div>
            <button className="stopButton" onClick={this.handleStatus}><h3>Stop</h3></button>
          </div>
          <div className="mainDiv">
            <h3>Status: {this.state.status}</h3>
          </div>
        </div>
    )}
    else {
      return (
        <div>
          <div>
            <button className="startButton" onClick={this.handleStatus}><h3>Start</h3></button>
          </div>
          <div className>
            <h3>Status: {this.state.status}</h3>
          </div>
        </div>
    )}
  }

}

export default StatusButton
