import React from 'react'

class Address extends React.Component {

  constructor() {
    super()
    this.state = {
      url: '',
      address: '192.168.0.100',
      port: '',
    }
  }

  componentDidMount() {
    this.setState (
      function() {
        return {
          url: this.props.baseURL
        }
      }
    )
  }

  render() {
    return (
      <div>
        <h2>Status</h2>
        <h3>Address: </h3><p><code>{this.state.address}</code></p>
      </div>
    )
  }

}

export default Address
