import React from 'react'

class startedButton extends React.Component {

  constructor() {
    super()
    this.state = {
      started: null,
    }
    this.handlestarted = this.handlestarted.bind(this)
  }

  componentsDidMount() {
    return null // get status
  }

  handlestarted() {
    this.setState (
      function() {
        if(this.state.started) {
          return {
            started: false
          }
        }
        else {
          return {
            started: true
          }
        }
      }
    )
  }

  render() {

    const styles = {
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center',
    }

    if(this.state.started) {
      return (
        <div>
          <div style={styles}><button className="stopButton" onClick={this.handlestarted}><h3>Stop</h3></button></div>
        </div>
    )}
    else {
      return (
        <div>
          <div style={styles}><button className="startButton" onClick={this.handlestarted}><h3>Start</h3></button></div>
        </div>
    )}
  }

}

export default startedButton
