import React from 'react'
import Header from './components/Header'
import StartStop from './components/StartStop'
import Status from './components/Status'
import Share from './components/Share'
import Login from './components/Login'
import { VerifyLogin } from './components/VerifyLogin'

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      url: 'http://3.128.29.141',
      login: false,
      username: '',
      password: ''
    }
    this.receiveLogin = this.receiveLogin.bind(this)
  }

  receiveLogin(loginUsername, loginPassword) {
    this.setState(
      function() {
        return {
          username: loginUsername,
          password: loginPassword,
          login: VerifyLogin(this.state.url, this.state.username, this.state.password)
        }
      }
    )
  }

  render() {

    const headerStyle = {
      width: '80%',
      fontSize: 30,
      fontWeight: 'bold',
      marginBottom: 15,
      textAlign: 'center',
      display: 'flex',
      justifyContent: 'center',
      alignCtems: 'center',
      padding: '3%',
    }

    if(this.state.login === true) {
      return (
        <div>
          <link rel="stylesheet" type="text/css" href="https://use.fontawesome.com/releases/v5.7.1/css/all.css" />
          <Header />
      		<div className="content">
            <StartStop baseURL={this.state.url}/>
            <Status baseURL={this.state.url}/>
            <Share baseURL={this.state.url} />
      		</div>
        </div>
    )}
    else if(this.state.login === false){
      return (
        <div className="appDiv">
          <link rel="stylesheet" type="text/css" href="https://use.fontawesome.com/releases/v5.7.1/css/all.css" />
          <h1 style={headerStyle}>Bedrock Server Controls</h1>
          <Login getCreds={this.receiveLogin}/>
        </div>
    )}
  }

}

export default App
