import React from 'react'
import Header from './components/Header'

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      address: '3.128.29.141',
      status: null
    }
    this.handleStatus = this.handleStatus.bind(this)
  }

  handleStatus() {
    this.setState (
      function() {
        if(this.state.status) {
          return {
            status: false
          }
        }
        else {
          return {
            status: true
          }
        }
      }
    )
  }

  render() {

      if(this.state.status) {
        return (
          <div>
            <Header />
            <div className="mainDiv">
              <h3>Status: Started</h3>
              <button className="stopButton" onClick={this.handleStatus}>Stop</button>
            </div>
          </div>
      )}

      else {
        return (
          <div>
            <Header />
            <div className="mainDiv">
              <button className="startButton" onClick={this.handleStatus}>Start</button>
            </div>
          </div>
      )}

  }

}

export default App;
