import React from 'react'

class StatusButton extends React.Component {

  constructor() {
    super()
    this.state = {
      status: null,
    }
    this.handleStatus = this.handleStatus.bind(this)
  }

  componentsDidMount() {
    return null
  }

  handleStatus() {
    this.setState (
      function() {
        if(this.state.status) {
          return {
            status: false
          }
        }
        else {
          return {
            status: true
          }
        }
      }
    )
  }

  render() {
    if(this.state.status) {
      return (
        <div className="mainDiv">
          <h3>Status: Started</h3>
          <button className="stopButton" onClick={this.handleStatus}>Stop</button>
        </div>
    )}
    else {
      return (
        <div className="mainDiv">
          <h3>Status: Stopped</h3>
          <button className="startButton" onClick={this.handleStatus}>Start</button>
        </div>
    )}
  }

}

export default StatusButton
