import React from "react"

class Register extends React.Component {

  constructor() {
    super()
    this.state = {
      username: "",
      email: "",
      password: "",
      password2: "",
      errors: {}
    }
  }

  onChange = e => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  onSubmit = e => {
    e.preventDefault()
    const newUser = {
      username: this.state.name,
      email: this.state.email,
      password: this.state.password,
      password2: this.state.password2
    }
    console.log(newUser)
  }

  render() {
    const {errors} = this.state

    return (
      <div className="appDiv">
        <h1 className="headerStyle">Bedrock Server Controls</h1>
        <div className="login">
          <h1>Register New User</h1>
          <form noValidate onSubmit={this.onSubmit}>
            <label>
              <input onChange={this.onChange} value={this.state.username} placeholder="Username" error={errors.username} id="username" type="text"/>
            </label>
            <label>
              <input onChange={this.onChange} value={this.state.email} placeholder="Email" error={errors.email} id="email" type="email"/>
            </label>
            <label>
              <input onChange={this.onChange} value={this.state.password} placeholder="Password" error={errors.password} id="password" type="password"/>
            </label>
            <label>
              <input onChange={this.onChange} value={this.state.password2} placeholder="Confirm Password" error={errors.password2} id="password2" type="password"/>
            </label>
            <input type="submit" value="Register" />
          </form>
        </div>
      </div>
    )

  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
})
Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
}
export default connect(mapStateToProps, { registerUser })(withRouter(Register))
