import React from 'react'

class StatusButton extends React.Component {

  constructor() {
    super()
    this.state = {
      status: null,
    }
    this.handleStatus = this.handleStatus.bind(this)
  }

  componentsDidMount() {
    return null
  }

  handleStatus() {
    this.setState (
      function() {
        if(this.state.status) {
          return {
            status: false
          }
        }
        else {
          return {
            status: true
          }
        }
      }
    )
  }

  render() {
    if(this.state.status) {
      return (
        <div>
          <div className="mainDiv">
            <button className="stopButton" onClick={this.handleStatus}><h3>Stop</h3></button>
          </div>
          <div className="mainDiv">
            <h3>Status: Started</h3>
          </div>
        </div>
    )}
    else {
      return (
        <div>
          <div className="mainDiv">
            <button className="startButton" onClick={this.handleStatus}><h3>Start</h3></button>
          </div>
          <div className="mainDiv">
            <h3>Status: Stopped</h3>
          </div>
        </div>
    )}
  }

}

export default StatusButton
