import React from 'react'
import QRCode from 'qrcode.react'

class Share extends React.Component {

  render() {

    const url = "http://" + this.props.address

    return (
      <div>
        <div className="mainDiv">
          <h3>Share this app</h3>
        </div>
        <div className="mainDiv">
          <QRCode
            id="qr-code"
            value={url}
            size={120}
            level={"H"}
            includeMargin={true}
          />
        </div>
      </div>
    )
  }

}

export default Share
