import React from 'react'

class Address extends React.Component {

  constructor() {
    super()
    this.state = {
      url: '',
      address: '',
      port: '',
    }
  }

  componentDidMount() {
    this.setState (
      function() {
        return {
          url: this.props.baseURL
        }
      }
    )
  }

  render() {
    return (
      <div>
        <h2>Address: {this.props.address}</h2>
      </div>
    )
  }

}

export default Address
